    }
    return (
        <div className = "add-treatment">
            <button>
                Back
            </button>
            <form>
                <label>Diagnosis</label>
                <textarea 
                required
                value={diagnosis}
                onChange={(e) => {setDiagnosis(e.target.value)}}
                ></textarea>
                <h4>Medicines</h4>
                <label>Select medicine</label>
                <select
                    value = {medicines}
                    onChange = {(e) => setMedicines(e.target.value)}
                >
                    <option value="Select Medicines">Select Medicines</option>
                    <option value="Crocin">Crocin</option>
                    <option value="Disprin">Disprin</option>
                </select>
                <label>Add tenure</label>
                <input 
                type="text" 
                required
                value = {tenure}
                onChange = {(e) => setTenure(e.target.value)}
                ></input>
                <label>Add Comments</label>
                <textarea 
                required
                value = {comments}
                onChange = {(e) => setComments(e.target.value)}
                ></textarea>
                <button
                onClick={handleMedicines}
                >Add medicine</button>
                { addMedicine && <MedicineList medicineObjects = {medicineArray}/>}
                <label>XRays</label>
                <textarea 
                required
                value = {xray}
                onChange = {(e) => setXray(e.target.value)}
                ></textarea>
                <label>Tests</label>
                <select
                value = {test}
                onChange = {(e) => setTest(e.target.value)}
                >
                    <option value="Select Tests">Select Tests</option>
                    <option value="Blood Test">Blood Test</option>
                    <option value="Covid Test">Covid Test</option>
                </select>
                <button>Add Test</button>
                <label>Physiotherapy</label>
                <textarea 
                required
                value = {physiotherapy}
                onChange = {(e) => setPhysiotherapy(e.target.value)}
                ></textarea>
                <button>Add Treatment</button>
                { addMedicine && <p>Hello World</p>}
            </form>
        </div>
    );
}
export default Form;
